public class Part2 {
    
    public int howMany(String stringa, String stringb) {
        
        int count = 0;
        int startIndex = stringb.indexOf(stringa);
        while (startIndex != -1) {
            count++;
            startIndex = stringb.indexOf(stringa, startIndex + stringa.length());
        }
        
        return count;
    }
    
    public void testhowMany() {
        
        String stringa = "GAA";
        String stringb = "ATGAACGAATTGAATC";
        int count = howMany(stringa, stringb);
        
        if (count != 3) System.out.println("Error");
        else            System.out.println("Occurs " + count);
        
        stringa = "AA";
        stringb = "ATAAAA";
        count = howMany(stringa, stringb);
        
        if (count != 2) System.out.println("Error");
        else            System.out.println("Occurs " + count);
        
        stringa = "CTG";
        stringb = "";
        count = howMany(stringa, stringb);
        System.out.println(count);
    }
}
